const menu=document.querySelector(".header__menu"),menu_icon=menu.querySelector(".menu__icon"),menu_body=menu.querySelector(".menu__body");menu_icon.addEventListener("click",e=>{e.preventDefault,menu_icon.classList.toggle("active"),menu_body.classList.toggle("active")});const map=document.querySelector(".map"),addresses=Array.from(map.querySelectorAll(".map__address")),images=Array.from(map.querySelectorAll(".map__preview img"));addresses.forEach((e,a)=>{e.addEventListener("click",e=>{e.preventDefault,addresses.forEach(e=>e.classList.remove("map__address-active")),images.forEach(e=>e.classList.remove("view"));let a=e.target.id.split("-")[1]-1;e.target.classList.add("map__address-active"),images[a].classList.add("view")})});